SELECT s.id AS songId, s.name AS songName, s.album, s.duration, s.style, s.cover_url AS coverUrl, s.audio_url AS audioUrl, s.release_time AS releaseTime, s.is_original AS isOriginal, s.is_reward_enabled AS isRewardEnabled, s.reward_qr_url AS rewardQrUrl, s.create_time AS createTime, s.creator_id AS creatorId, s.audit_status AS auditStatus, u.username AS creatorName FROM tb_song s LEFT JOIN tb_user u ON s.creator_id = u.id WHERE s.creator_id = #{userId} AND s.is_original = true
AND s.audit_status = #{auditStatus}
ORDER BY s.create_time DESC
